function Flu_CHL = Fluoro_Swims(FluV, m, b)% Flu_CHL = Fluoro_Swims(FluV, m, b);% Compute chlorophyll-a from SeaPoint Fluorometer data;%   FluV = raw voltages from Fluorometer;%   m,b = slope,offset calibration coefficients%   Flu_CHL = concentration in micrograms/Liter%% Note that cal factor should be:  % (verified 6-Mar-2004, DPW)%%   m = [30, 10, 3, 1] for gain cable = [1X, 3X, 10X, 30X]%   DPW - apr/2002Flu_CHL = FluV * m + b;