function Obs_FTU = Obs_Swims(ObsV, m, b, q)% Obs_FTU = Obs_Swims(ObsV, m, b);% Compute optical backscatter;%   ObsV = raw voltages from OBS-3;%   m,b = slope,offset calibration coefficients;%   q = quadratic term for OBS-3+ (added oct/2011 - DPW)%   Obs_FTU = backscatter in NTUs(FTUs?) - Nephelometric (Formazin?)%       turbidity units%   DPW - apr/2002if nargin<4 || isempty(q) || ~isnumeric(q)    q = 0;endObs_FTU = ObsV.^2*q + ObsV*m + b;