function Rol_ang = roll_Swims(Rvolt, Version, factor)% Rol_ang = roll_Swims(Rvolt, Version, factor);% Compute roll angle from SWIMS2 voltage;%   Rvolt = 0 to 5 Volts from A-D (Version=2, default),%       or -10 to 10 Volts (Version=1 for SWIMS 1);%   factor = optional gain/sense factor (default = 1)%   Rol_ang = roll angle in degrees% Desired convention: right (stb) side down > 0, up < 0if nargin<2 || isempty(Version)    Version = 2; % for SWIMS 2endif nargin<3    if Version==2        factor = 1; % default for (min:max V) = (-1g:+1g)    elseif Version==3        factor = -1; % SWIMS 3 (verified 21-Feb-2008)    else        factor = -1; % SWIMS 1 (chk 11-Mar-2004)     endendaccOVg = NaN;if Version == 2 || Version==3    accOVg = (Rvolt - 2.5) / 2.5; % accel/G for SWIMS2,3elseif Version == 1    accOVg = (Rvolt - 0) / 10; % accel/G for SWIMS1end% dominant accel signal = g*sin(tilt);% leading -1 factor gives proper sense for SWIMS2 rollRol_ang = -1 * asin(accOVg) * (180/pi) * factor; 