function [lat,lon,topo]=PSBathy;%Use these for all of PS%latmin=47.0;%latmax=48.5;%lonmin=122.2;%lonmax=123.4;%This is the original header for the file.ncols =        257;nrows =        532;%xllcorner     484365%yllcorner     5208495cellsize  =    300;NODATA_value =  -9999;%load -ascii Shortboard:Data:PSBathy:bth300_2_nohdr.ascglobal harddriveeval(['load -ascii ' harddrive 'Data' filesep 'PSBathy' filesep 'bth300_2_nohdr.asc'])%var name is bth300_2_nohdr%earth radiusre=6.3767e+006;%xGPS=(lonm-lonm(i1))*2*pi*re*cos(lat/180*pi)/360;%yGPS=(latm-latm(i1))*2*pi*re/360;% coords of SW corner of map in degreeslato=47.032;lono=123.206;%x and y are in metersx=(0:(ncols-1))*cellsize;y=fliplr((0:(nrows-1))*cellsize);lat=lato+y*360/2/pi/re;%lon=lono-x*360/2/pi/re/cos(lato/180*pi);%Change for 3/01: I now use 48 (in the middle) instead of the %lower corner for the longitude conversion.  It's not exactly %right but is close.latmid=48;lon=lono-x*360/2/pi/re/cos(latmid/180*pi);land=600;topo=bth300_2_nohdr;%stick land values topo(find(topo==66))=-300;