function mapax=PSBathyPlot(latmin,latmax,lonmin,lonmax)%function mapax=PSBathyPlot(latmin,latmax,lonmin,lonmax)%Plot up the shoreline using the 30-m data from David Finlayson.%The old version, PSShorePlot300, used the 300-m data from Miles Logsdon.%not complete yet!PSBathy30Path='D:\SWIMS_MHA\PSbathy';shorefile=fullfile(PSBathy30Path,'PSTM_shoreline.mat');bathyfile=fullfile(PSBathy30Path,'PSsub_isobaths.mat');eval(['shoreline=load(''' shorefile ''');'])eval(['load(''' bathyfile ''')'])dep1=50;dep2=100;dep3=150;dep4=200;ib1 = find([IsoBath(:).depth] == dep1);ib2 = find([IsoBath(:).depth] == dep2);ib3 = find([IsoBath(:).depth] == dep3);ib4 = find([IsoBath(:).depth] == dep4);% h=plot(-shoreline.lon_sh,shoreline.lat_sh,...%     -IsoBath(ib1).lon,IsoBath(ib1).lat,'k--',...%     -IsoBath(ib2).lon,IsoBath(ib2).lat,'k-',...%     -IsoBath(ib3).lon,IsoBath(ib3).lat,'k--',...%     -IsoBath(ib4).lon,IsoBath(ib4).lat,'k-'...%     );h=plot(-shoreline.lon_sh,shoreline.lat_sh,...    -IsoBath(ib1).lon,IsoBath(ib1).lat,'k-',...    -IsoBath(ib2).lon,IsoBath(ib2).lat,'m-',...    -IsoBath(ib3).lon,IsoBath(ib3).lat,'g-',...    -IsoBath(ib4).lon,IsoBath(ib4).lat,'r-'...    );lw(h(1),2);set(gca,'XDir','reverse')%Now get the isolines for other contoursxlabel('West Longitude / degrees') ylabel('North Latitude / degrees') ff=.001;axis([lonmin-ff lonmax+ff latmin-ff latmax+ff ])dar=get(gca,'dataaspectratio');latmid=1/2*(latmin+latmax);dar(1)=dar(1).*cos(latmid/180*pi);set(gca,'plotboxaspectratio',dar)set(gca,'box','on')mapax=gca;