function mapax=PSBathyPlot(latmin,latmax,lonmin,lonmax)%function mapax=PSBathyPlot(latmin,latmax,lonmin,lonmax)%Plot up the PS bathymetry from Miles Logsdon.  [lat,lon,topo]=PSBathy;rat=(latmax-latmin)/(lonmax-lonmin);%lat/lon ratio.%indlon=find(lon > lonmin & lon < lonmax);indlat=find(lat > latmin & lat < latmax);%Antoher try -- this works great.land=50;topo(find(topo>-1))=land;[c,h]=contourf(lon(indlon),lat(indlat),topo(indlat,indlon),[-300 -250 -200 -150 -100 -50 50]);set(h,'EdgeColor','none')set(gca,'XDir','reverse')hold on[c,h]=contour(lon(indlon),lat(indlat),topo(indlat,indlon),[-150 -100 -50],'k');[c2,h2]=contour(lon(indlon),lat(indlat),topo(indlat,indlon),[0 0],'k-');set(h2,'LineWidth',2)hold offset(gca,'XDir','reverse')gm=[.4 .5 .6 .7 .8 1]'*[1 1 1];%gm=(0:(1/16):1)'*[1 1 1];colormap(gm)%ylabel('Latitude / degrees')xlabel('West Longitude / degrees') ylabel('North Latitude / degrees') ff=.001;axis([lonmin-ff lonmax+ff latmin-ff latmax+ff ])dar=get(gca,'dataaspectratio');latmid=48;dar(1)=dar(1).*cos(latmid/180*pi);set(gca,'plotboxaspectratio',dar)set(gca,'box','on')mapax=gca;