function mapax=PSShorePlot(latmin,latmax,lonmin,lonmax)%function mapax=PSShorePlot(latmin,latmax,lonmin,lonmax)%Plot up the shoreline using the 30-m data from David Finlayson.%The old version, PSShorePlot300, used the 300-m data from Miles Logsdon.%PSBathy30Path='D:\SWIMS_MHA\PSbathy'shorefile=fullfile(PSBathy30Path,'PSTM_shoreline.mat');eval(['shoreline=load(''' shorefile ''')'])plot(-shoreline.lon_sh,shoreline.lat_sh)ff=.001;axis([lonmin-ff lonmax+ff latmin-ff latmax+ff ])rat=(latmax-latmin)/(lonmax-lonmin);set(gca,'XDir','reverse')ff=.001;axis([lonmin-ff lonmax+ff latmin-ff latmax+ff ])%ylabel('Latitude / degrees')xlabel('West Longitude / degrees') ylabel('North Latitude / degrees') dar=get(gca,'dataaspectratio');latmid=1/2*(latmin+latmax);dar(1)=dar(1).*cos(latmid/180*pi);set(gca,'plotboxaspectratio',dar)set(gca,'box','on')mapax=gca;