function WriteJPEG(name, prpath,flags)%function WriteEPS(name, prpath, quality)%Write an eps file with the current figure.  If no path is specified,%the default path (set in the global DEFAULT_PRINT_FOLDER) is used.  %If that is not specified, the current directory is used.%Leave off the .eps.%Don't put spaces in the path -- it doesn't like it.%use SetDefaultPrintFolder(path) to set the default path.global DEFAULT_PRINT_FOLDER;if ~exist('flags')	flags='';else     flags=num2str(flags);endif ~exist('prpath')	prpath=DEFAULT_PRINT_FOLDER;endif isempty(prpath)	path=cd;enddisp(['Printing figure to file ' prpath name '.jpg'])eval(['print -djpeg' flags ' ' prpath name '.jpg'])