function ZapTick(wh);%function ZapTick(wh);%Specify 'l', 'r', 't' or 'b' and ZapTick will remove the specified tick and label. if strcmp(wh,'l') 	xl=get(gca,'XTick');	xl=xl(2:length(xl));	set(gca,'XTick',xl)elseif strcmp(wh,'r')	xl=get(gca,'XTick');	xl=xl(1:length(xl)-1);	set(gca,'XTick',xl)elseif strcmp(wh,'t')	xl=get(gca,'YTick');	xl=xl(1:length(xl)-1);	set(gca,'YTick',xl)elseif strcmp(wh,'b')	xl=get(gca,'YTick');	xl=xl(2:length(xl));	set(gca,'YTick',xl)else	disp 'unrecognized string: use l,r,t or b.'end