function h=tb(x,y,str,arr,sz)%function h=tb(x,y,str,arr,size)%Place a bold-type string str at location x,y.%If arr=1, a left arrow is placed before the text.  If not (default), just the%text is used.%If size is specified, set the font size.if ~exist('arr')	arr=0;endif arr==1	str=['\leftarrow' str];endh=text(x,y,str);set(h,'FontWeight','Bold')if exist('sz')	set(h,'FontSize',sz)end