function h=tbr(x,y,str,arr,sz)%function h=tbr(x,y,str,arr,size)%Place a right-justified bold-type string str at location x,y.%If arr=1, a right arrow is placed after the text.  If not (default), just the%text is used.%If size is specified, set the font size.if ~exist('arr')	arr=0;endif arr==1	str=[str '\rightarrow' ];endh=text(x,y,str);set(h,'FontWeight','Bold')if exist('sz')	set(h,'FontSize',sz)endset(h,'horizontalalignment','right')