%function y=ErrorBars(deg,conlevel,x,y,width,linewidth)%Plot error bars on current loglog plot.  On such a plot all we need to know are the%dof's and the desired confidence limits.  Also specify location and width relative to axes.%Passing 0,0,0 for x,y and width causes default values to be used.%6/14/00 modified to do linear x axes right.function y=ErrorBars(deg,conlevel,x,y,width,linewidth)c=ConfLims(deg,conlevel);cl=c(1);ch=c(2);a=get(gca,'XLim');b=get(gca,'YLim');if width==0	width=1/20;endif linewidth==0	linewidth=1;endxloc=x;yloc=y;if xloc==0	xloc=a(1)*10;	yloc=b(1)*10;endstr=get(gca,'XScale');if strcmp(str,'log')==1	plwidth=10^(width*(log10(a(2)) - log10(a(1))));	xmid=10^(.5*(log10(xloc*plwidth) + log10(xloc)));	hold on	h=loglog([xloc xloc*plwidth],[yloc*cl yloc*cl]);	set(h,'LineWidth',linewidth);	h=loglog([xmid xmid],[yloc*cl yloc*ch]);	set(h,'LineWidth',linewidth);	h=loglog([xloc xloc*plwidth],[yloc*ch yloc*ch]);	set(h,'LineWidth',linewidth);		hold off	else	plwidth=width*(a(2) - a(1));	xmid=.5*(xloc+plwidth + xloc);	linewidth	hold on	h=plot([xloc xloc+plwidth],[yloc*cl yloc*cl]);	set(h,'LineWidth',linewidth);	h=plot([xmid xmid],[yloc*cl yloc*ch]);	set(h,'LineWidth',linewidth);	h=plot([xloc xloc+plwidth],[yloc*ch yloc*ch]);	set(h,'LineWidth',linewidth);		hold off	end