function [Altimcoefs]=read_AltimCal_SWIMS(sn,year,yday)%function [Altimcoefs]=read_AltimCal_SWIMS(sn,year,yday)%Return the cal coefficients for the DataSonics or TriTech Altimeter% for a given sensor S/N, year and yearday.% The function searches the global directory stored in SWIMS_cal/Altim for% a file whose name is the desired S/N or designation, which contains% a list of dates and coefficients for each calibration.  It reads the last% ones before the desired date and returns them in structure Altimcoefs.%  10-2003 dpwglobal SWIMS_calAltimcoefs.Mo=NaN;dtnum_for = datenum(year,1,1) + yday;%open this file.filename=fullfile(SWIMS_cal, 'Altim' , sn);fid=fopen(filename,'r');if fid == -1	disp(['Error: no cal file exists for Altim S/N=' sn])	return;end%Read the first three header lines.line=fgetl(fid); line=fgetl(fid); line=fgetl(fid);done=0; cfs = [];count=1;line=fgetl(fid);while isstr(line)    dstr = [line(6:7) '/' line(9:10) '/' line(1:4) ' ' ...            line(12:13) ':' line(15:16) ':' line(18:19)]; % mm/dd/yyyy hh:mm:ss    dtnum_cal(count) = datenum(dstr);    [dstr,x] = strtok(line);    % There are only two, but save coeff sets in cfs(cal#,:) anyway    for i=1:100        [cstr,x] = strtok(x);        cfs(count,i) = str2num(cstr);        % all entries in file must have same number of coeffs        if isempty(deblank(x))            break        end    end	count=count+1;		line=fgetl(fid);endfclose(fid);%Find the last calibration before the desired date.ind = find(dtnum_cal<=dtnum_for);if isempty(ind)    disp(['No calib for S/N=' sn ' in effect for ' datestr(dtnum_for)])else    ind=ind(end);    Altimcoefs.Mo=cfs(ind,1); Altimcoefs.Bo=cfs(ind,2); end