function [H2SCoefs]=read_H2SCal_SWIMS(sn,year,yday)%function [H2SCoefs]=read_H2SCal_SWIMS(sn,year,yday)%Return the H2S (sulphide) cal coefficients for a given S/N, year and yearday.%The function searches the global directory stored in SWIMS_cal/H2S for%a file whose name is the desired S/N, which contains a list of dates%and coefficients for each calibration.  It reads the last ones before %the desired date and returns them in structure H2SCoefs.%  feb,2003 - dpw (for ?? model)global SWIMS_calH2SCoefs.UGoff=NaN;dtnum_for = datenum(year,1,1) + yday;%open this file.filename=fullfile(SWIMS_cal, 'H2S' , sn);fid=fopen(filename,'r');if fid == -1	disp(['Error: no cal file exists for H2S S/N=' sn])	return;end%Read the first three header lines.line=fgetl(fid); line=fgetl(fid); line=fgetl(fid);done=0; cfs = [];count=1;line=fgetl(fid);while isstr(line)    dstr = [line(6:7) '/' line(9:10) '/' line(1:4) ' ' ...            line(12:13) ':' line(15:16) ':' line(18:19)]; % mm/dd/yyyy hh:mm:ss    dtnum_cal(count) = datenum(dstr);    [dstr,x] = strtok(line);    % Save coeff sets in rows of cfs(cal#,:)     for i=1:100        [cstr,x] = strtok(x);        cfs(count,i) = str2num(cstr);        % all entries in file must have same number of coeffs        if isempty(deblank(x))            break        end    end	count=count+1;		line=fgetl(fid);endfclose(fid);%Find the last calibration before the desired date.ind = find(dtnum_cal<=dtnum_for);if isempty(ind)    disp(['No calib for S/N=' sn ' in effect for ' datestr(dtnum_for)])else    ind=ind(end);    H2SCoefs.aT0=cfs(ind,1); H2SCoefs.aT1=cfs(ind,2);    H2SCoefs.aT2=cfs(ind,3); H2SCoefs.aT3=cfs(ind,4);    H2SCoefs.a20C=cfs(ind,5); H2SCoefs.UGoff=cfs(ind,6);    % NOTE: UGoff is zero (H2S) offset in **milliVolts**end