function [TPRcoefs]=read_TPRcal_sbe_SWIMS(sn,year,yday)%function [TPRcoefs]=read_TPRcal_sbe_SWIMS(sn,year,yday)%Return the cal coefficients for the temperature compensation word% for a given pressure sensor S/N, year and yearday.% The function searches the global directory stored in SWIMS_cal/P for% a file whose name is the desired S/N with '_TC' appended, which contains% a list of dates and coefficients for each calibration.  It reads the last% ones before the desired date and returns them in structure TPRcoefs.%  4-2002 dpwglobal SWIMS_calTPRcoefs.m=NaN;dtnum_for = datenum(year,1,1) + yday;%open this file.filename=fullfile(SWIMS_cal, 'P' , [sn '_TC']);fid=fopen(filename,'r');if fid == -1	disp(['Error: no cal file exists for Pres S/N=' sn '_TC'])	return;end%Read the first three header lines.line=fgetl(fid); line=fgetl(fid); line=fgetl(fid);done=0; cfs = [];count=1;line=fgetl(fid);while isstr(line)    dstr = [line(6:7) '/' line(9:10) '/' line(1:4) ' ' ...            line(12:13) ':' line(15:16) ':' line(18:19)]; % mm/dd/yyyy hh:mm:ss    dtnum_cal(count) = datenum(dstr);    [dstr,x] = strtok(line);    % There are only two, but save coeff sets in cfs(cal#,:) anyway    for i=1:100        [cstr,x] = strtok(x);        cfs(count,i) = str2num(cstr);        % all entries in file must have same number of coeffs        if isempty(deblank(x))            break        end    end	count=count+1;		line=fgetl(fid);endfclose(fid);%Find the last calibration before the desired date.ind = find(dtnum_cal<=dtnum_for);if isempty(ind)    disp(['No calib for S/N=' sn '_TC in effect for ' datestr(dtnum_for)])else    ind=ind(end);    TPRcoefs.m=cfs(ind,1); TPRcoefs.b=cfs(ind,2); end