function [pHCoefs]=read_pHCal_SWIMS(sn,year,yday)%function [pHCoefs]=read_pHCal_SWIMS(sn,year,yday)%Return the pH sensor cal coefficients for a given S/N, year and yearday.%The function searches the global directory stored in SWIMS_cal/pH for%a file whose name is the desired S/N, which contains a list of dates%and coefficients for each calibration.  It reads the last ones before %the desired date and returns them in structure pHCoefs.%  feb,2003 - dpw (for SBE 18 model)global SWIMS_calpHCoefs.offset=NaN;dtnum_for = datenum(year,1,1) + yday;%open this file.filename=fullfile(SWIMS_cal, 'pH' , sn);fid=fopen(filename,'r');if fid == -1	disp(['Error: no cal file exists for pH S/N=' sn])	return;end%Read the first three header lines.line=fgetl(fid); line=fgetl(fid); line=fgetl(fid);done=0; cfs = [];count=1;line=fgetl(fid);while isstr(line)    dstr = [line(6:7) '/' line(9:10) '/' line(1:4) ' ' ...            line(12:13) ':' line(15:16) ':' line(18:19)]; % mm/dd/yyyy hh:mm:ss    dtnum_cal(count) = datenum(dstr);    [dstr,x] = strtok(line);    % Save coeff sets in rows of cfs(cal#,:)     for i=1:100        [cstr,x] = strtok(x);        cfs(count,i) = str2num(cstr);        % all entries in file must have same number of coeffs        if isempty(deblank(x))            break        end    end	count=count+1;		line=fgetl(fid);endfclose(fid);%Find the last calibration before the desired date.ind = find(dtnum_cal<=dtnum_for);if isempty(ind)    disp(['No calib for S/N=' sn ' in effect for ' datestr(dtnum_for)])else    ind=ind(end);    pHCoefs.slope=cfs(ind,1);    pHCoefs.offset=cfs(ind,2); end